CodeChunks.CodeChunk:

foreach (var plugin in PluginManager.Plugins.Where(p => p.Plugin.Name == "Enable Flight" || p.Plugin.Name == "启用飞行"))
{
  try
  {
    if (!plugin.Enabled)  {  break;  }  else  {  plugin.Enabled = false;  }
  }
  catch (Exception ex) {  }
}


var v1 = new Vector3(-325.8211f, 69.62695f, 301.1016f);
var v2 = new Vector3(-179.3403f, 61.45739f, 81.59988f);
var v3 = new Vector3(-176.2334f, 52.09153f, 53.77029f);
var p1 = new ff14bot.Pathing.FlyToParameters(v1) { CheckIndoors = false };
var p2 = new ff14bot.Pathing.FlyToParameters(v2) { CheckIndoors = false };
var p3 = new ff14bot.Pathing.FlyToParameters(v3) { CheckIndoors = false };

await CommonTasks.TakeOff();

while (Core.Player.Location.Distance(v1) > 3f)
{
   ff14bot.Pathing.Flightor.MoveTo(p1);
   await Coroutine.Yield();
}

while (Core.Player.Location.Distance(v2) > 3f)
{
   ff14bot.Pathing.Flightor.MoveTo(p2);
   await Coroutine.Yield();
}

while (Core.Player.Location.Distance(v3) > 2f)
{
   ff14bot.Pathing.Flightor.MoveTo(p3);
   await Coroutine.Yield();
}
